// Sample code for calculating GPA conversion
function convertGPA(gpa, fromScale, toScale) {
  // Convert to 4.0 scale first
  const normalizedGPA = (gpa / fromScale) * 4.0;

  // Then convert to target scale
  return (normalizedGPA / 4.0) * toScale;
}

// Example: Convert 3.7 GPA from 4.0 to 100-point scale
const convertedGPA = convertGPA(3.7, 4.0, 100);
console.log(`Converted GPA: ${convertedGPA.toFixed(1)}`);
